import React, { useState } from 'react'


const EmailField = () => {

    const [email, setEmail] = useState('');
    const [emailErr, setEmailErr] = useState(false);

    const handleEmailChange = (e) => {

        e.preventDefault();

      let email = e.target.value;

        // check for email format
        let regex = /^\w+[\w-\.]*\@\w+((-\w+)|(\w*))\.[a-z]{2,3}$/;

        // if format is valid, set email; else, return error.
        if ( regex.test(email) ) {
          setEmailErr(false);
          setEmail(email);
        }
        else {
          setEmailErr(true);
        }

    }

  return (

    <span className='grid grid-rows-3'>

      <div className='row-span-2 grid grid-cols-2 grid-rows-1 '>
        <label className=' flex items-end justify-center'>
          Email:
        </label>
        <input className='mx-3' type="email"
              onBlur={ handleEmailChange }
          />
      </div>

      {emailErr &&
          <div className='row-span-1 flex items-center justify-center mx-3 text-red-500 text-sm'>
            Invalid email.
          </div>  
      }

      </span>

  )
}

export default EmailField
