import React, { useState} from 'react'
import { MenuIcon, UserIcon, XIcon } from '@heroicons/react/outline'
import DummyLogo from "../assets/dummy_logo.png"
import Button from './Button';



const LoginBar = () => {
    // Logic functions to control when menu and what hamburger menu displays.
    const [menu, setMenu] = useState(false);
    const handleClickMenu = () => setMenu(!menu);
    const [register, setRegister] = useState(false);
    const handleClickRegister = () => setRegister(!register);
    const [login, setLogin] = useState(false);
    const handleClickLogin = () => setLogin(!login);

  return (
    <div className='h-navbar w-screen bg-zinc-200 grid grid-cols-2 grid-rows-1 grid-flow-col fixed top-0 drop-shadow-lg '>

          {/* Hold the logo and project name. */}
          <div className='flex items-center justify-start col-span-2'>
              <img className="h-small-logo w-small-logo mx-4" src={ DummyLogo } alt="Gilderise Enterprise Logo" ></img>
              <p className='font-cursive tracking-wider'> Gilderise Enterprise</p>
          </div>

          {/* Hold the responsive hamburger menu and login buttons or login buttons by themselves. */}
          <div className='flex items-center justify-end'>
              {/* Hamburger menu only shows when screen size is small; otherwise display login/register buttons. */}
              <div onClick={handleClickMenu} className='md:hidden'>
                  {!menu? <MenuIcon className='h-xsmall-logo w-xsmall-logo mx-6'/>: <XIcon className='h-xsmall-logo w-xsmall-logo mx-4'/>}
              </div>

              {/* Login buttons show by themselves when screen is sufficiently big. */}
              <div className='hidden md:flex '>
              <Button
                height="h-small-button"
                width='min-w-max'
                color='bg-c_rich_electric_blue'
                buttonText='testing'
                textColor='text-c_white'
                hoverColor='hover:bg-c_blue_pigment'
              />

              </div>
          </div>

    </div>

  )
}

export default LoginBar
