import React, { useRef, useState } from 'react'
import { ChevronRightIcon, ChevronLeftIcon } from '@heroicons/react/outline';

const Property = ({ property }) => {

    const [currIndex, setCurrIndex] = useState(0);
    const maxLength = property.images.length;

    const prevPic = () => {
        setCurrIndex(currIndex === 0? (maxLength - 1) : (currIndex - 1));
     };
    const nextPic = () => {
        setCurrIndex(currIndex === (maxLength - 1) ? 0 : (currIndex + 1));
     };



	return (
		<div className='h-full w-full m-4'>
			<div className='p-6 border border-solid rounded-md grid grid-row-2'>
				{/* Image */}
                <div className='row-span-1 snap-mandatory snap-x relative'>
                    <ChevronLeftIcon className='h-xsmall-logo absolute top-[50%] left-2 cursor-pointer select-none' onClick={prevPic} />
                    <ChevronRightIcon className='h-xsmall-logo absolute top-[50%] right-2 cursor-pointer select-none' onClick={nextPic} /> 
                    {property.images.map((img, index) => {
                        return (
                            <div
                            className={index === currIndex ? 'opacity-10 scale-110 ease-out duration-200' : 'opacity-0'}
                            key={index}
                            >
                                <img src={img} alt={"Beautiful Property"} />
                                </div>
                        ) })}
				</div>
				{/* Everything Else */}
				<div>

				</div>

			</div>
		</div>
	)
}

export default Property
