                                    height="h-xsmall-button"
                                    color='bg-zinc-400'
                                    buttonText='Submit'
                                    textColor='text-c-white'
                                    hoverColor='hover:bg-zinc-500'
                                />
                            </ button>

                            <button onClick={handleClickLogin}>
                                <Button
                                    height="h-xsmall-button"
                                    color='bg-zinc-400'
                                    buttonText='Cancel'
                                    textColor='text-c-white'
                                    hoverColor='hover:bg-zinc-500'
                                />
                            </button>

                            <button onClick={handleForgotPassword}>
                                <Button
                                    height="h-xsmall-button"
                                    color='bg-zinc-400'
                                    buttonText='Forgot Password'
                                    textColor='text-c-white'
