}


  return (
    <div>

      {/* start actual code */}
      <nav className='h-navbar w-screen bg-zinc-200 grid grid-cols-2 grid-rows-1 fixed top-0 drop-shadow-lg '>

        {/* Logo and project name. */}
        <section className='flex items-center justify-start '>
            <img className="h-small-logo w-small-logo mx-4" src={ DummyLogo } alt="Gilderise Enterprise Logo" ></img>
            <p className='text-xl font-cursive tracking-wider'> Gilderise Enterprise</p>
        </section>

        {/* Responsive hamburger menu and login buttons or login buttons by themselves. */}
        <section className='flex items-center justify-end'>

          {/* Hamburger menu only shows when screen size is small; otherwise display login/register buttons. */}
          <div onClick={handleClickMenu} className='md:hidden'>
              {!menu? <MenuIcon className='h-xsmall-logo w-xsmall-logo mx-6'/>: <XIcon className='h-xsmall-logo w-xsmall-logo mx-4'/>}
          </div>

          {/* Login buttons show by themselves when screen is sufficiently big. These display in right -> left format */}
          <section className='hidden md:flex '>

            {/* Login Button and login logic*/}
            <button onClick={handleClickLogin}>

              <Button
                height="h-small-button"
                color='bg-zinc-400'
                buttonText='Login'
                textColor='text-c_white'
                hoverColor='hover:bg-zinc-500'
              />
            </button>

            {/* Register Button and registration logic*/}
            <button onClick={handleClickRegister}>
              <Button
                height="h-small-button"
                color='bg-zinc-400'
                buttonText='Register'
                textColor='text-c_white'
                hoverColor='hover:bg-zinc-500'
                />
            </button>

            {/* dropdown login menu */}
            <form onSubmit={handleSubmit}>
              {
                !login ? <div className='hidden'>
                  {/* Empty */}
                </div> :

                // display menu
                  <div className=' absolute left-[50%] top-[4.5rem] h-dropdown-menu w-[50%] bg-zinc-100 grid grid-rows-5'>

                    {/* Email field */}
                    <div className='row-span-2 my-4'>
                      < EmailField />
                    </div>

                    {/* Password field */}
                    <div className='row-span-2 flex items-center justify-center'>
                      < PasswordField />
                    </div>

                    {/* "Submit" and "Cancel Button" */}
                    <div className='row-span-1 flex items-center justify-end'>
                      < button >
                        <Button
                          height="h-xsmall-button"
                          color='bg-zinc-400'
                          buttonText='Submit'
                          textColor='text-c_white'
                          hoverColor='hover:bg-zinc-500'
                        />
                      </ button>

                      <button onClick={handleClickLogin}>
                        <Button
                          height="h-xsmall-button"
                          color='bg-zinc-400'
                          buttonText='Cancel'
                          textColor='text-c_white'
                          hoverColor='hover:bg-zinc-500'
                          />
                      </button>
                    </div>


                </div>
              }
            </form>



          </section>

        </section>

      </nav>
      {/* end code */}

    </div>

  )
}

export default LoginBar
